export default {
  name: 'vForm',
  props: {
    model: {
      type: Object
    },
    rules: {
      type: Object
    }
  },
  data: function data() {
    return {
      fileds: []
    };
  },
  created: function created() {
    var _this = this;

    this.$on("on-form-item-add", function (field) {
      if (field) _this.fileds.push(field);
    });
    this.$on("on-form-item-remove", function (field) {
      if (field.prop) _this.fileds.splice(_this.fileds.indexOf(field), 1);
    });
  }
};
